    def cadastrar(self):
        nome=self.line_nome.text()
        codigo=self.lineEdit.text()
        fabrica=self.line_fabricante.text()
        estoque=self.spinBox_estoque.text()
        validade=self.date.date().toPyDate()
        valor_compra=self.doubleSpin_compra.text()
        valor_venda=self.doubleSpin_venda.text()

       
        categoria=self.combo_categoria.currentText()


        controlada=False
        if self.checkBox.isChecked():
            controlada=True   

        
        medida=""
        if self.radio_ml.isChecked():
            medida="Militros (ml)"
        elif self.radio_mg.isChecked(): 
            medida="Micrograma (mcg)"
        elif self.radio_mg.isChecked():
            medida="Miligramas (mg)"
        elif self.radio_g.isChecked():
            medida="Gramas (g)"
        elif self.radio_litro.isChecked():
            medida="Litro (L)"

        descricao= self.text_descricao.toPlainText()   


        sql="INSERT INTO produtos VALUES(%s, %s,%s,%s,%s,%s,%s,%s,%s,%s,%s)"
        cursor.execute(sql,(codigo, nome, fabrica, estoque, validade, valor_compra, valor_venda, controlada, medida, descricao, categoria ))
        conexao.commit()
        print("Inserido com sucesso.")


        msg=QMessageBox()
        msg.setWindowTitle("Aviso")
        msg.setText("INSERIDO COM SUCESSO.")
        msg.exec()


        self.line_nome.setText("")
        self.line_fabricante.setText("")
        self.spinBox_estoque.setValue(0)
        self.lineEdit.setText("")
        self.doubleSpin_compra.setValue(0)
        self.doubleSpin_venda.setValue(0)
        self.combo_categoria.setCurrentIndex(0)
        self.radio_ml.setChecked(True)
        self.checkBox.setChecked(False)
        self.text_descricao.setPlainText("")    

